import { useEffect, useState } from "react";
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
// import Updatenewpost from "./Updatenewpost";
// import axios from 'axios';
import axios from "axios";


import dateFormat from 'dateformat';
import { NavLink } from "react-router-dom";

export default function Singlepostview(props) {
    const [getid, setGetid] = useState([])
    // useEffect(() => {
    //     // if (props.postid != undefined) {
    //     const headers = { 'Content-Type': 'application/json' }
    //     let url = "http://localhost:3001/showfiles/" + props.postid;
    //     fetch(url, { headers })
    //         .then(response => response.json())
    //         .then(data =>
    //             setGetid(data)
    //         );
    //     // axios.get(url, headers).then((res) => {
    //     //     console.log(res),
    //     //         setGetid(res)
    //     // })
    //     // axios.get(url,headers)
    //     //     .then(response => {
    //     //         setGetid(response)
    //     //     });
    //     // }
    // }, [])

    useEffect(() => {
        getvalue();
    }, [])

    const getvalue = () => {
        axios.get('http://localhost:3001/showfiles/128')
            .then(response => {
                console.log(response.data);
                setGetid(response.data);
            }).catch(error => {
                console.error("Error", error);
            })
    }



    console.log(getid);
    return (<div>
            <div className="col-md" key={i}>
                <div className="card" style={{ width: 'rem' }}>
                    <img src={"http://127.0.0.1:3001/" + item.file_name} className="card-img-top" alt="..." style={{ height: '13rem' }} />
                    <div className="card-body">
                        <h5 className="card-title fw-bold"><NavLink to={"/postview/" + item.id} className="text-decoration-none">{item.title.substring(0, 70) + "..."}</NavLink></h5>
                        <p className="card-text">{item.description.substring(3, 70) + "..."} </p>
                        <div className="text-center"><NavLink to={"/postview/" + item.id} class="btn btn-primary" postid='' >Read More</NavLink></div>
                    </div>
                </div>
            </div>
        )}

        {
            getid !== null ?
                <post>
                    <article >
                        {/* <!-- Post header--> */}
                        <header className="mb-4">
                            {/* <!-- Post title--> */}
                            <h1 className="fw-bolder mb-1">{getid.title}</h1>
                            {/* <!-- Post meta content--> */}
                            {/* <div className="text-muted fst-italic mb-2">Posted on {dateFormat(getid.uploaded_on, "dddd, mmmm dS, yyyy")} by Ashish  </div> */}
                            {/* <!-- Post categories--> */}
                            <a className="badge bg-secondary text-decoration-none link-light" href="#!">Web Design</a>
                            <a className="badge bg-secondary text-decoration-none link-light" href="#!">Freebies</a>
                        </header>
                        {/* <!-- Preview image figure--> */}
                        {/* <figure className="mb-4"><img className="img-fluid rounded" src={"http://127.0.0.1:8000/" + getid.file_name} width="900" height="300" alt="..." /></figure> */}

                        <figure className="mb-4"><img className="img-fluid rounded" src={"http://localhost:3001/" + getid.file_name} width="900" height="300" alt="..." /></figure>
                        {/* <!-- Post content--> */}
                        <section className="mb-5">
                            {ReactHtmlParser(getid.description)}
                        </section>
                    </article>


                    <section className="mb-5">
                        <div className="card bg-light">
                            <div className="card-body">
                                {/* <!-- Comment form--> */}
                                <form className="mb-4"><textarea className="form-control" rows="3" placeholder="Join the discussion and leave a comment!"></textarea></form>
                                {/* <!-- Comment with nested comments--> */}
                                <div className="d-flex mb-4">
                                    {/* <!-- Parent comment--> */}
                                    <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                    <div className="ms-3">
                                        <div className="fw-bold">Commenter Name</div>
                                        If you're going to lead a space frontier, it has to be government; it'll never be private enterprise. Because the space frontier is dangerous, and it's expensive, and it has unquantified risks.
                                        {/* <!-- Child comment 1--> */}
                                        <div className="d-flex mt-4">
                                            <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                            <div className="ms-3">
                                                <div className="fw-bold">Commenter Name</div>
                                                And under those conditions, you cannot establish a capital-market evaluation of that enterprise. You can't get investors.
                                            </div>
                                        </div>
                                        {/* <!-- Child comment 2--> */}
                                        <div className="d-flex mt-4">
                                            <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                            <div className="ms-3">
                                                <div className="fw-bold">Commenter Name</div>
                                                When you put money directly to a problem, it makes a good headline.
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                {/* <!-- Single comment--> */}
                                <div className="d-flex">
                                    <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                    <div className="ms-3">
                                        <div className="fw-bold">Commenter Name</div>
                                        When I look at the universe and all the ways the universe wants to kill us, I find it hard to reconcile that with statements of beneficence.
                                    </div>
                                </div>
                            </div>
                        </div>
                    </section>
                </post>

                : <h1>Not Found</h1>
        }

    </div>);
}
