import { useEffect, useState } from "react";

export default function Addnewpost() {

    let profile = JSON.parse(localStorage.getItem('profile'));
    const [error, setError] = useState("");
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const file_name = "doc/iStock-841135958.jpg";
    const account = profile.name;

    // if(title === ""){
    //     setError("Enter the title");
    // }

    function titlefuncton(e){
        let titledata = e.target.value;
        if(titledata.length < 2){
            setError("Enter the title");
        }else{
            setTitle(title)
        }
    }
    function descriptionfuncton(e){
        let descriptiondata = e.target.value;
        if(descriptiondata.length < 2){
            setError("Enter the description");
        }
    }

    async function addarticle(e) {

        e.preventDefault();
        let data = localStorage.getItem('user-info');
        let info = JSON.parse(data);
        let postdata = { title, description, file_name, account }
        let result = await fetch('http://127.0.0.1:8000/api/insertfile', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                'Authorization': 'Bearer ' + info.access_token
            },
            body: JSON.stringify(postdata)
        });

        result = await result.json();
        if (result.message === "Inserted Successfully") {
            setError(
                <div class="alert alert-success" role="alert">
                    Inserted Successfully
                </div>
        );
        setTitle("");
        setDescription("");
        } else {
            setError(
                <div class="alert alert-danger" role="alert">
                    Inserted Unsuccessfully
                </div>
            );
        }
    }

    return (<>

        <section className="masthead">
            <div className="container px-5 ">
                <div className="row d-flex justify-content-center" >
                    <div className="col-md-6 ">

                        <h1 className="h3 mb-3 font-weight-bold text-center">Add Post</h1>
                        <label for="inputEmail" className="sr-only">Title</label>
                        <input type="text" className="form-control" placeholder="Enter Title" value={title} onChange={titlefuncton} required />
                        <label for="inputPassword" className="sr-only">Description</label>
                        <input type="text" className="form-control" placeholder="Enter Description" value={description} onChange={ descriptionfuncton} required />

                        <button className="btn btn-lg btn-primary btn-block" onClick={addarticle} >Add Post</button>
<br/><br/>
                        {error ? error : null}
                    </div>
                </div>
            </div>
        </section>
    </>);
}
