import axios from "axios";
import { useState } from "react";
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import {useHistory } from 'react-router-dom';
// import Username from "../User/Username";
import { Alert,Spinner } from 'react-bootstrap';
import { useEffect } from "react";
export default function Addnewpost() {

    const [pname, setPname] = useState('');
    const [error, setError] = useState('');
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [imagedata, setDImagedata] = useState("");
    const history = useHistory();

    const handleChange = (file) => {
        setDImagedata(file[0]);
    };

    const handleChangeckeditor = (e,editor) => {
        const data = editor.getData();
        setDescription(data);
    }

    useEffect(()=>{
        let data = JSON.parse(localStorage.getItem('user-info'));
        setPname(data); 
    })

    

    async function addarticle(e) {
        
        // let profile = JSON.parse(localStorage.getItem('profile'));
        const account = pname.name;

        e.preventDefault();
        const fData = new FormData();
        fData.append("filename", imagedata);
        fData.append("title", title);
        fData.append("description", description);
        fData.append("account", account);

        axios.post("http://localhost:3001/insertdata",fData)
        .then((res) => {
            console.log(res.data.status);
            if(res.data.status == 200){
                setError(["success","Inserted Successfully","border"]);
                // setTitle('');
                // setDescription('');
                // setDImagedata('');
                setTimeout(() => { 
                    history.push("/dashboard")
                 }, 3000);

            }             
        }).catch(error =>{console.log("Error",error)})
    }

    return (<>
        {/* <Username /> */}
        <section className="">
            <div className=" ">
                <div className="" >
                    <div className=" ">
                        <h1 className="h3 mb-3 font-weight-bold text-center">Add Post</h1>
                        {error != '' ? (<Alert variant={error[0]} >{error[1]} <Spinner animation={error[2]} size="sm" /></Alert>) : null}
                           

                        <label for="" className="sr-only">Title</label>
                        <input type="text" className="form-control" placeholder="Enter Title" value={title} onChange={(e) => setTitle(e.target.value)} /><br />
                        <label for="" className="sr-only">Description</label>
                        {/* <input type="text" className="form-control" placeholder="Enter Description" value={description} onChange={(e) => setDescription(e.target.value)} /><br /> */}

                        <CKEditor editor={ClassicEditor} data={description} onChange={handleChangeckeditor} />

                        <label for="uploadfile" className="sr-only">Upload File </label>
                        <input name="image" id="image" type="file" onChange={(e) => handleChange(e.target.files)} />
                        <br /><br />
                        <button className="btn btn-md btn-primary btn-block" onClick={addarticle} >Add Post</button>
                        <br /><br />
                    </div>
                </div>
            </div>
        </section>
    </>);
}
