import { useState } from "react";
import { useEffect } from "react";

import Nav from './Nav'
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle";
// import CodingImg from './image/18824957.jpg'
import CodingImg from './image/codingimg.png'

import { BrowserRouter as Router, Route, Switch, NavLink } from 'react-router-dom'


export default function Home() {
    
    const [sitedata, setSitedata] = useState([])
    useEffect(() => {
        fetch('http://127.0.0.1:8000/api/showfile').then((result) => {
            result.json().then((resp) => {
                setSitedata(resp)
            })

        })
    }, [])

    return (
        <div>
            <Nav />
            {/* <!-- Mashead header--> */}
            <header className="masthead">
                <div className="container px-5">
                    <div className="row gx-5 align-items-center">
                        <div className="col-lg-5">
                            {/* <!-- Mashead text and app badges--> */}
                            <div className="mb-5 mb-lg-0 text-center text-lg-start">
                                <h1 className="display-4  mb-3 fw-bold">Showcase your app <span className="text-info">beautifully</span>.</h1>
                                <p className="lead fw-normal text-muted mb-3">Launch your mobile app landing page faster with this free, open source theme.</p>
                                <button type="button" className="btn btn-outline-info rounded-pill">Get Started</button>

                            </div>
                        </div>
                        <div className="col-lg-7">
                            {/* <!-- Masthead device mockup feature--> */}
                            <div className="masthead-device-mockup">
                                <div className="device-wrapper">
                                    <div className="device" data-device="iPhoneX" data-orientation="portrait" data-color="black">
                                        <div className="screen bg-black">
                                            <img src={CodingImg} width="100%" />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </header>

            <newtrend/>





        </div>
    )
}


 function newtrend() {

    return (<>
        <div className="masthead1 bg-light">
            <div className="container px-5 mb-5">

                <div className="text-center display-6 fw-bold mb-5 ">New Trend</div>
                <div className=" row row-cols-1 row-cols-md-3 g-4">

                    {sitedata.map((item, i) =>
                        <div className="col" key={i}>
                            <div className="card" style={{ width: 'rem' }}>
                                <img src={"http://127.0.0.1:8000/" + item.file_name} className="card-img-top" alt="..." style={{ height: '13rem' }} />
                                <div className="card-body">
                                    <h5 className="card-title fw-bold">{item.title.substring(0, 70) + "..."}</h5>
                                    <p className="card-text">{item.description.substring(3, 70) + "..."} </p>
                                    <div className="text-center"><NavLink to={"/postview/" + item.id} class="btn btn-primary" postid='' >Read More</NavLink></div>
                                </div>
                            </div>
                        </div>

                    )}
                </div>
            </div>
        </div>
    </>);
}
