import Nav from './Nav';
import Navigation from './Components/All/navigation';
import Username from "./Components/User/Updateprofile";
import {useState,useEffect} from 'react';
export default function User(){

    // const [fetchemail,setfetchemail]= useState('');
    // const [fetchname,setfetchname]= useState('');
    const [name,setName] = useState("");
    const [email,setEmail] = useState("");
    const [password,setPassword]= useState("");

useEffect(()=>{
    setName(JSON.parse(localStorage.getItem('user-info')).name)
    setEmail(JSON.parse(localStorage.getItem('user-info')).email)
},[])

    const onsubmitform = (e)=>{
        e.preventDefault()
        console.log(name,email,password)
    }
    console.log(JSON.parse(localStorage.getItem('user-info')).name,"name")

    return(<>
        <Nav/>
        <span className="text-capitalize"><Navigation  navigation={"Edit"}/>
        </span>



        <div class="masthead1 container">
            <div class="row gx-5 align-items-center justify-content-center">
            <div class="col-6 card0 p-md-5">
            <ul class="nav nav-pills mb-3" id="pills-tab" role="tablist">
  <li class="nav-item">
    <a class="nav-link active" id="pills-home-tab" data-toggle="pill" href="#pills-home" role="tab" aria-controls="pills-home" aria-selected="true">Home</a>
  </li>
  <li class="nav-item">
    <a class="nav-link" id="pills-profile-tab" data-toggle="pill" href="#pills-profile" role="tab" aria-controls="pills-profile" aria-selected="false">Profile</a>
  </li>
  <li class="nav-item">
    <a class="nav-link" id="pills-contact-tab" data-toggle="pill" href="#pills-contact" role="tab" aria-controls="pills-contact" aria-selected="false">Contact</a>
  </li>
</ul>
<div class="tab-content" id="pills-tabContent">
  <div class="tab-pane fade show active" id="pills-home" role="tabpanel" aria-labelledby="pills-home-tab">...</div>
  <div class="tab-pane fade" id="pills-profile" role="tabpanel" aria-labelledby="pills-profile-tab">...</div>
  <div class="tab-pane fade" id="pills-contact" role="tabpanel" aria-labelledby="pills-contact-tab">...</div>
</div>
                <form class="was-validated" >
                    <label for="validationServer03">Name</label>
                    <input type="text" class="form-control is-valid" id="validationServer03" defaultValue={name} onChange={(e)=>{setName(e.target.value)}} placeholder="Enter Name..." required/>
                    <br/>
                    <label for="validationServer01">Email</label>
                    <input type="email" class="form-control is-valid" id="validationServer01" defaultValue={email} placeholder="Enter Email..." onChange={(e)=>{setEmail(e.target.value)}} required/>
                    <br/>
                    <label for="validationServer02">Password</label>
                    <input type="password" class="form-control is-valid" id="validationServer02" placeholder="Enter Password..." onChange={(e)=>{setPassword(e.target.value)}} required/>
                    <br/>
                    
                    <input type="submit" value="Update" onClick={(e)=>onsubmitform(e)}/>
                </form>
            </div>
            </div>
        </div>

    </>);
}
