import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import { Alert, Spinner } from 'react-bootstrap';
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import InputTags from "react-input-tags-hooks";
import 'react-input-tags-hooks/build/index.css'; // required

export default function Updatenewpost(props) {
    const [error, setError] = useState("");
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [viewimage, setViewimage] = useState("");
    const [imagedata, setDImagedata] = useState("");
    const [array, setArray] = useState('');
    const [pname, setPname] = useState('');
    const [tags, setTags] = useState([])
    // let { id } = useParams();

    const getTags = (tags) => {
        setTags(tags);
    }

    let url = "http://localhost:3001/showfiles/" + props.Postid;

    const fetchlist = () => {
        axios.get(url).then(response => {
            setArray(response.data[0])
        })
    }

    useEffect(() => {
        fetchlist();
        setPname(JSON.parse(localStorage.getItem('user-info')));
    }, [])

    const handleChangeckeditor = (e, editor) => {
        const data = editor.getData();
        setDescription(data);
    }
    async function handlefile(file) {
        setDImagedata(file[0]);
        setViewimage(URL.createObjectURL(file[0]))
    }

    async function updatearticle(e) {
        e.preventDefault();
        const account = pname.name;
        const fData = new FormData();
        fData.append("filename", imagedata);
        fData.append("title", title);
        fData.append("description", description);
        fData.append("account", account);
        fData.append("tag", tags);
        let url = "http://localhost:3001/updatedata/" + props.Postid;
        axios.post(url, fData)
            .then((res) => {
                console.log(res);
                if (res.data.status == 200) {

                    setError(["success", "Updated Successfully", "border"]);
                    setTimeout(() => {
                        setError([]);
                    }, 3000);

                }
            });
        console.log(fData)
    }

    return (<>
        <section className="">
            <div className=" ">
                <div className="row" >
                    <div className="col-md-6">
                        <h1 className="h3 mb-3 font-weight-bold text-center">Update Post</h1>
                        <label for="inputEmail" className="sr-only">Title</label>
                        <input type="text" className="form-control" placeholder={"Enter Title"} defaultValue={array.title} onChange={(e) => { setTitle(e.target.value) }} /><br />
                        <label for="" className="sr-only">Description</label>
                        <CKEditor editor={ClassicEditor} data={array.description} style={stylingObject.ckeditor} onChange={handleChangeckeditor} />
                        <br />
                        <label for="uploadfile" className="sr-only">Upload File </label>
                        <input name="file_name" id="filename" type="file" onChange={(e) => handlefile(e.target.files)} />
                        <br />
                        <br />
                        <label for="Tag" className="sr-only">Enter Tags </label>
                        <InputTags
                            onTag={getTags}
                            tags={tags}
                            tagColor={'#48c774'}
                            data={tags}
                            placeHolder="Press enter to add tags"
                        /><br /><br />
                        <button className="btn btn-md btn-primary btn-block" onClick={(e) => { 
                            
                            if (title == '' || description == '' || imagedata == '' || tags == '') {
                                setError(["danger", "Fill the fields"]);
                                setTimeout(() => { setError('') }, 3000);
                            } else{
                            updatearticle(e) }}} >Update Post</button>
                        <br /><br />
                        {error != '' ? (<Alert variant={error[0]} >{error[1]} <Spinner animation={error[2]} size="sm" /></Alert>) : null}

                    </div>
                    <div className="col-md-6">
                        <article>
                            <h2 className="text-center pb-3">Preview</h2>
                            {/* <!-- Post header--> */}
                            <header className="mb-4">
                                {/* <!-- Post title--> */}
                                <h4 className="fw-bolder mb-1 text-break">{title}</h4>
                                {/* <!-- Post meta content--> */}
                                {/* <div className="text-muted fst-italic mb-2">Posted on {dateFormat(item.uploaded_on, "dddd, mmmm dS, yyyy")} by {item.account}  </div> */}
                                {/* <!-- Post categories--> */}
                                {tags}
                                <a className="badge bg-secondary text-decoration-none link-light" href="#!">Web Design</a>
                                <a className="badge bg-secondary text-decoration-none link-light" href="#!">Freebies</a>
                            </header>
                            {/* <!-- Preview image figure--> */}
                            <figure className="mb-4"><img className="img-fluid rounded" src={viewimage == '' ? ("http://localhost:3001/" + array.file_name) : viewimage} width="900" height="300" alt="..." /></figure>
                            {/* <!-- Post content--> */}
                            <section className="mb-5 text-break">
                                {ReactHtmlParser(description)}
                            </section>
                        </article>
                    </div>
                </div>

            </div>
        </section>
    </>);
}


var stylingObject = {
    ckeditor: {
        minHeight: "400px"
    }
}
