import axios from "axios";
import { useEffect, useState } from "react";
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import Username from "../User/Username";
export default function Addnewpost() {

    const [error, setError] = useState("");
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [imagedata, setDImagedata] = useState("");

    const [addData, setVal] = useState("");
    

    const handleChange = (file) => {
        setDImagedata(file[0]);
    };

    const handleChangeckeditor = (e,editor)

    async function addarticle(e) {
        
        let profile = JSON.parse(localStorage.getItem('profile'));
        let data = localStorage.getItem('user-info');
        const account = profile.name;

        e.preventDefault();
        const fData = new FormData();
        fData.append("image", imagedata);
        fData.append("title", title);
        fData.append("description", description);
        fData.append("account", account);

        // const result = await fetch("http://127.0.0.1:8000/api/upload-image", {
        //     method: "POST",
        //     body: fData,
        // })
        axios.post("http://127.0.0.1:8000/api/insertfile",fData)
        .then((res) => {
            // console.log("response",res);
            // console.log("response",res.data.message);
            if (res.data.message === "Success") {
                    setError(
                        <div class="alert alert-success" role="alert">
                            Inserted Successfully
                        </div>
                    );
                    setTitle("");
                    setDescription("");
                    setDImagedata("");
                }else if (res.data.message === "Select the Image") {
                    setError(
                        <div class="alert alert-danger" role="alert">
                            Upload File
                        </div>
                    );
                }
                else {
                    setError(
                        <div class="alert alert-danger" role="alert">
                            Inserted Unsuccessfully
                        </div>
                    );
        
                }
               
        });

    };

