    //             "Accept": "application/json",
    //             'Authorization': 'Bearer ' + info.access_token
    //         },
    //         body: JSON.stringify(postdata)
    //     });

    //     result = await result.json();
        // if (result.message === "Inserted Successfully") {
        //     setError(
        //         <div class="alert alert-success" role="alert">
        //             Inserted Successfully
        //         </div>
        //     );
        //     setTitle("");
        //     setDescription("");
        // } else if (result.message === "Upload File") {
        //     setError(
        //         <div class="alert alert-danger" role="alert">
        //             Upload File
        //         </div>
        //     );
        // }
        // else {
        //     setError(
        //         <div class="alert alert-danger" role="alert">
        //             Inserted Unsuccessfully
        //         </div>
        //     );

        // }

    //     console.log(postdata)
    // }

    return (<>
        <Username />
        <section className="">
            <div className=" ">
                <div className="" >
                    <div className=" ">
                        <h1 className="h3 mb-3 font-weight-bold text-center">Add Post</h1>
                        <label for="" className="sr-only">Title</label>
                        <input type="text" className="form-control" placeholder="Enter Title" value={title} onChange={(e) => setTitle(e.target.value)} /><br />
                        <label for="" className="sr-only">Description</label>
                        <input type="text" className="form-control" placeholder="Enter Description" value={description} onChange={(e) => setDescription(e.target.value)} /><br />

                        <CKEditor editor={ClassicEditor} data={addData} onChange={handleChangeckeditor} />

                        <label for="uploadfile" className="sr-only">Upload File </label>
                        <input name="image" id="image" type="file" onChange={(e) => handleChange(e.target.files)} />
                        <br /><br />
                        <button className="btn btn-md btn-primary btn-block" onClick={addarticle} >Add Post</button>
                        <br /><br />
                        {error ? error : null}
                    </div>
                </div>
            </div>
        </section>
    </>);
}
