import Nav from './Nav'
import Trends from './Components/Trends/Trends'
import axios from 'axios'
import { useEffect, useState } from 'react'
import Select from 'react-select'
import Button from '@restart/ui/esm/Button'

export default function Home() {

  const [search, setSearch] = useState('live');




  const options = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
  ]


  useEffect(() => {
    searchpost();
  })
  const searchpost = () => {
    axios.post('http://localhost:3001/search', search).then(response => {
      console.log(response);
    }).catch(error => {
      console.error("Error", error);
    })
  }

  return (<>
    <Nav />

    <div className="masthead1 bg-light">

      <div className="container px-5 mb-5">
        <div className="text-center display-6 fw-bold mb-5 ">ALL POST</div>
        <div className="text-center pb-md-5">
          <div className="searchs" style={stylingObject.searchs} >
            <Select options={options}  onChange={()=>{setSearch}}/>
            <Button variant="primary" onc>Search</Button>
          </div>
        </div>

        <Trends />
      </div>
    </div>

  </>)
}



var stylingObject = {
  searchs: {
    display: "inline-block",
    justifyContent: "center",
    width: "40%",
  }
}
