import { useState } from "react";
import { useEffect } from "react";
import { useParams } from "react-router-dom"
import axios from 'axios';
import dateFormat from 'dateformat';

import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
export default function Postview() {

    const [getid, setGetid] = useState([])
    const { id } = useParams();
    useEffect(() => {
        fetch('http://127.0.0.1:8000/api/files/').then((result) => {
            result.json().then((resp) => {
                setGetid(resp)
            })
        })
    }, [getid])


    let numberAsInt = parseInt(id, 10);
    // useEffect(()=>{
    //     loadPost();
    // },[])
    // const loadPost = async() =>{
    //     const res = await axios.get('http://127.0.0.1:8000/api/files/${id}');
    //     setGetid(res.getid)
    // }
    return (
        <div>


            <div className="container mt-5 px-5">
                <div className="row">
                    {
                        getid.map((item, i) =>

                            item.id === numberAsInt ? 

                                <div className="col-lg-8" >
                                    {/* <!-- Post content--> */}
                                    <article  key={i}>
                                        {/* <!-- Post header--> */}
                                        <header className="mb-4">
                                            {/* <!-- Post title--> */}
                                            <h1 className="fw-bolder mb-1">{item.title}</h1>
                                            {/* <!-- Post meta content--> */}
                                            <div className="text-muted fst-italic mb-2">Posted on January 1, 2021 by Start Bootstrap {item.uploaded_on} {console.log(new Intl.DateTimeFormat('en-US', {year: 'numeric', month: '2-digit',day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit'}).format(today))}</div>
                                            {/* <!-- Post categories--> */}
                                            <a className="badge bg-secondary text-decoration-none link-light" href="#!">Web Design</a>
                                            <a className="badge bg-secondary text-decoration-none link-light" href="#!">Freebies</a>
                                        </header>
                                        {/* <!-- Preview image figure--> */}
                                        <figure className="mb-4"><img className="img-fluid rounded" src="https://dummyimage.com/900x400/ced4da/6c757d.jpg" alt="..." /></figure>
                                        {/* <!-- Post content--> */}
                                        <section className="mb-5">
                                           {item.description}
                                        </section>
                                    </article>
                                    {/* <!-- Comments section--> */}
                                    <section className="mb-5">
                                        <div className="card bg-light">
                                            <div className="card-body">
                                                {/* <!-- Comment form--> */}
                                                <form className="mb-4"><textarea className="form-control" rows="3" placeholder="Join the discussion and leave a comment!"></textarea></form>
                                                {/* <!-- Comment with nested comments--> */}
                                                <div className="d-flex mb-4">
                                                    {/* <!-- Parent comment--> */}
                                                    <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                                    <div className="ms-3">
                                                        <div className="fw-bold">Commenter Name</div>
                                                        If you're going to lead a space frontier, it has to be government; it'll never be private enterprise. Because the space frontier is dangerous, and it's expensive, and it has unquantified risks.
                                                        {/* <!-- Child comment 1--> */}
                                                        <div className="d-flex mt-4">
                                                            <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                                            <div className="ms-3">
                                                                <div className="fw-bold">Commenter Name</div>
                                                                And under those conditions, you cannot establish a capital-market evaluation of that enterprise. You can't get investors.
                                                            </div>
                                                        </div>
                                                        {/* <!-- Child comment 2--> */}
                                                        <div className="d-flex mt-4">
                                                            <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                                            <div className="ms-3">
                                                                <div className="fw-bold">Commenter Name</div>
                                                                When you put money directly to a problem, it makes a good headline.
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                                {/* <!-- Single comment--> */}
                                                <div className="d-flex">
                                                    <div className="flex-shrink-0"><img className="rounded-circle" src="https://dummyimage.com/50x50/ced4da/6c757d.jpg" alt="..." /></div>
                                                    <div className="ms-3">
                                                        <div className="fw-bold">Commenter Name</div>
                                                        When I look at the universe and all the ways the universe wants to kill us, I find it hard to reconcile that with statements of beneficence.
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </section>
                                </div>

                                : null
                        )
                    }


                    <div div className="col-lg-4">

                        <div className="card mb-4">
                            <div className="card-header">Search</div>
                            <div className="card-body">
                                <div className="input-group">
                                    <input className="form-control" type="text" placeholder="Enter search term..." aria-label="Enter search term..." aria-describedby="button-search" />
                                    <button className="btn btn-primary" id="button-search" type="button">Go!</button>
                                </div>
                            </div>
                        </div>
                        {/* <!-- Categories widget--> */}
                        <div className="card mb-4">
                            <div className="card-header">Categories</div>
                            <div className="card-body">
                                <div className="row">
                                    <div className="col-sm-6">
                                        <ul className="list-unstyled mb-0">
                                            <li><a href="#!">Web Design</a></li>
                                            <li><a href="#!">HTML</a></li>
                                            <li><a href="#!">Freebies</a></li>
                                        </ul>
                                    </div>
                                    <div className="col-sm-6">
                                        <ul className="list-unstyled mb-0">
                                            <li><a href="#!">JavaScript</a></li>
                                            <li><a href="#!">CSS</a></li>
                                            <li><a href="#!">Tutorials</a></li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </div>
                        {/* <!-- Side widget--> */}
                        <div className="card mb-4">
                            <div className="card-header">Side Widget</div>
                            <div className="card-body">You can put anything you want inside of these side widgets. They are easy to use, and feature the Bootstrap 5 card component!</div>
                        </div>
                    </div>
                </div>
            </div>




        </div >

    )

}
