import axios from "axios";
import { useEffect, useState } from "react";
import { NavLink, Route } from "react-router-dom";

export default function Listpost() {

  const [postJSON, setPostJSON] = useState([]);
  const [loading, setLoading] = useState(false);
  const [pname, setPname] = useState("");
  const [token, setToken] = useState("");

  
  useEffect(() => {
    
    setPname(JSON.parse(localStorage.getItem('user-info')));
    console.log(pname)
    
    if (pname === '' || pname === undefined) {
      console.log("Token is not there!")
  } else {
      setToken(pname.token);
      console.log("token",token);
  }
    fetchimage()
  }, []);


  const fetchimage = async (e) => {
    let data = localStorage.getItem('user-info');
    let info = JSON.parse(data)
    console.log("parse",info);
    const header = { 'Content-Type': 'application/json'
    , 'Authorization': 'Bearer ' + token 
  }
    // fetch('http://127.0.0.1:8000/api/showfile/', { header })
    //   .then(response => response.json())
    //   .then(data1 => setPostJSON(data1))

    await axios.get('/showfiles/',header)
    .then(response=>{
      console.log(response);
      setPostJSON(response.data);
    })
    setLoading(true);

  }

  const deleteImage = (productId) => {
    axios.get('http://127.0.0.1:3001/delete/' + productId)
      .then((result) => {
        alert("Delete successfully")
        fetchimage();
      })
      .catch(() => {
        alert('Error in the Code');
      });
  };

  return (<>


    {loading ? (
      
      postJSON != '' ?
      <table class="table">
        <thead>
          <tr>
            <th scope="col">Sr.No</th>
            <th scope="col">Image</th>
            <th scope="col">Title</th>
            <th scope="col">View</th>
            <th scope="col">Edit</th>
            <th scope="col">Delete</th>
          </tr>
        </thead>
        <tbody>
          {
            postJSON.map((item, i) =>

              <tr key={i}>
                <th scope="row">{i + 1}</th>
                <th><img src={"http://127.0.0.1:3001/" + item.file_name} alt="..." width="50" />
                </th>
                <td>{item.title}</td>
                <td><NavLink to={"/postview/" + item.id} className="text-decoration-none">View</NavLink></td>
                <td><NavLink to={"/postedit/" + item.id} className="text-decoration-none">Edit</NavLink></td>
                <td><NavLink to="#" onClick={() => deleteImage(item.id)} className="text-decoration-none" >Delete</NavLink></td>
              </tr>
            )
          }

        </tbody>

      </table>
      
      : ( <h4 class="text-center pt-3">Post Not Found</h4>)
    ) : (

      <div class="d-flex justify-content-center">
        <div class="spinner-border m-5" role="status">
          <span class="visually-hidden">Loading...</span>
        </div>
      </div>
    )}


  </>);
}
