import { useParams } from "react-router-dom"
import Nav from './Nav';
import Singlepostview from "./Components/Post/Singlepostview";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import { useState } from "react";
// import Listpost from "./Components/Sidebar/Listpost";
import axios from "axios";
export default function Postview() {

    const { id } = useParams();
    const [searchinput,setsearchinput] = useState("");
    const [Postdata, setPostdata] = useState("");


    const header = {
        'Content-Type': 'application/json'
    }
    axios.get('/showfiles', { method: 'GET', header })
        .then(response => {
            console.log(response.data);
            setPostdata(response.data);
            console.log("post",Postdata)
        })




    const searchonclick = (e)=>{
      if(searchinput !== ''){
        window.location.href = "/service/search/" + searchinput
      }
        console.log(searchinput);
    }
    return (

        <div>
            <Nav />
            <div className="container mt-5 px-5">
                <div className="row">

                    <div className="col-lg-8" >

                    <Singlepostview postid={id}/>

                        
                    </div>




                    <div div className="col-lg-4">

                        <div className="card mb-4">
                            <div className="card-header">Search</div>
                            <div className="card-body">
                                <div className="input-group">
                                    <input className="form-control" type="text" placeholder="Enter search term..." onChange={(e)=>{setsearchinput(e.target.value)}} aria-label="Enter search term..." aria-describedby="button-search" />
                                    <button className="btn btn-primary"  type="submit" id="button-search" onClick={(e)=>{
                                        if(searchinput !== '' ){
                                            searchonclick();
                                        }
                                    }} type="button">Go!</button>
                                  
                                </div>
                            </div>
                        </div>
                        {/* <!-- Categories widget--> */}
                        <div className="card mb-4">
                            <div className="card-header">Categories</div>
                            <div className="card-body">
                                <div className="row">
                                    <div className="col-sm-6">
                                        <ul className="list-unstyled mb-0">
                                            <li><a href="#!">Web Design</a></li>
                                            <li><a href="#!">HTML</a></li>
                                            <li><a href="#!">Freebies</a></li>
                                        </ul>
                                    </div>
                                    <div className="col-sm-6">
                                        <ul className="list-unstyled mb-0">
                                            <li><a href="#!">JavaScript</a></li>
                                            <li><a href="#!">CSS</a></li>
                                            <li><a href="#!">Tutorials</a></li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </div>
                        {/* <!-- Side widget--> */}
                        <div className="card mb-4">
                            <div className="card-header">Side Widget</div>

                            {
                Postdata.map((item, i)=>{
                    <ul key={i}>
                        <li>{item.title}</li>
                    </ul>
                })
            }
                            <div className="card-body">You can put anything you want inside of these side widgets. They are easy to use, and feature the Bootstrap 5 card component!</div>
                        </div>
                    </div>
                </div>
            </div>




        </div >

    )

}
