import { useEffect, useState } from "react";

import Username from "../User/Username";
export default function Addnewpost() {

    const [error, setError] = useState("");
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [file_name, setFile_name] = useState("");


    // if(title === ""){
    //     setError("Enter the title");
    // }

    // function titlefuncton(e){
    //     let titledata = e.target.value;
    //     if(titledata.length === 0){
    //         setError("Enter the title");
    //     }else{
    //         setTitle(titledata);
    //     }
    // }
    // function descriptionfuncton(e){
    //     let descriptiondata = e.target.value;
    //     if(descriptiondata.length === 0){
    //         setError("Enter the description");
    //     }else{
    //         setDescription(descriptiondata);
    //     }
    // }

    const handleChange = (file) =>{
        setFile_name(file[0]);
    }
    console.log(file_name);

    async function addarticle(e) {

        e.preventDefault();
        
        let profile = JSON.parse(localStorage.getItem('profile'));
        let data = localStorage.getItem('user-info');
        const account = profile.name;

        const filedata = new FormData();
        const image=filedata.append("file_name",file_name)

        let info = JSON.parse(data);
            let postdata = { title, description, filedata, account }
            let result = await fetch('http://127.0.0.1:8000/api/insertfile', {
                method: 'POST',
                headers: {
                    "Content-Type": "application/json",
                    "Accept": "application/json",
                    'Authorization': 'Bearer ' + info.access_token
                },
                body: JSON.stringify(postdata)
            });

            result = await result.json();
            if (result.message === "Inserted Successfully") {
                setError(
                    <div class="alert alert-success" role="alert">
                        Inserted Successfully
                    </div>
                );
                setTitle("");
                setDescription("");
            } else {
                setError(
                    <div class="alert alert-danger" role="alert">
                        Inserted Unsuccessfully
                    </div>
                );
                
            }
            
    console.log(postdata)
    }

    return (<>
        <Username />
        <section className="">
            <div className=" ">
                <div className="" >
                    <div className=" ">
                        <h1 className="h3 mb-3 font-weight-bold text-center">Add Post</h1>
                        <label for="" className="sr-only">Title</label>
                        <input type="text" className="form-control" placeholder="Enter Title" value={title} onChange={(e) => setTitle(e.target.value)}  /><br />
                        <label for="" className="sr-only">Description</label>
                        <input type="text" className="form-control" placeholder="Enter Description" value={description} onChange={(e) => setDescription(e.target.value)}  /><br />
                        <label for="uploadfile" className="sr-only">Upload File </label>
                        <input type="file" className="form-control" placeholder="Enter File"  onChange={(e) => handleChange(e.target.files)}  />
                        <br />
                        <button className="btn btn-md btn-primary btn-block" onClick={addarticle} >Add Post</button>
                        <br /><br />
                        {error ? error : null}
                    </div>
                </div>
            </div>
        </section>
    </>);
}
