import Nav from './Nav'
import Trends from './Components/Trends/Trends'
import axios from 'axios'
import { useEffect, useState } from 'react'
import Select from 'react-select'
import { Alert, Button, Form, Row, Col, InputGroup, FormControl } from 'react-bootstrap';
// import Button from '@restart/ui/esm/Button'
import { NavLink } from "react-router-dom";

export default function Home() {

  const [search, setSearch] = useState('');
  const [array, setArray] = useState('');
  const [postdata, setPostdata] = useState('');
  
  const [searchapi, setSearchapi] = useState('');
  const [loading, setLoading] = useState(false);
  // console.log(options)

  // const option = [
  //   { value: search, label: search },
  //   { value: 'chocolate', label: 'Chocolate' },
  //   { value: 'chocolate', label: 'Chocolate' },
  //   { value: 'chocolate', label: 'Chocolate' },
  // ]


  useEffect(() => {
    fetchlist();
  }, [])



  let url = "http://localhost:3001/showfiles/";

  const fetchlist = () => {
    axios.get(url).then(response => {
      setArray(response.data);
    })
  }
  console.log(array);

  // const handleChange = e => {
  //   setSearch(e.target.value);
  // }
  function searchpostonclick(e) {
    e.preventDefault();
    setSearchapi(search);
  }

  return (<>
    <Nav />
    <div className="masthead1 bg-light">
      <div className="container px-5 mb-5">
        <div className="text-center display-6 fw-bold mb-5 ">ALL POST</div>
        {/* <div className="text-center pb-md-5"> */}
        {/* <div className="searchs" style={stylingObject.searchs} > */}
        {/* <Select option={option} onChange={(e)=>{ handleChange(e); setOptions(e.target.value)}} /> */}
        {/* <Button variant="primary" onClick={(e) => { searchpostonclick(e) }}>Search</Button> */}
        {/* </div> */}
        {/* </div> */}
        {/* <div > */}
        <div className="pb-md-5 d-flex justify-content-center" >
          <form>
            <div className="row align-items-center">
              <div className="col" xs="auto">
                <input
                  type="text"
                  className="mb-2 form-control"
                  id="inlineFormInput"
                  placeholder="Search"
                  onChange={(e) => { setSearch(e.target.value) }}
                />
              </div>
              <div className="col" xs="auto">
                <button className="btn btn-primary mb-2" onClick={(e) => {
                  if (search == '') {
                    setSearchapi();
                    console.log(sea)
                    
                  } else {
                    setSearchapi('');
                    searchpostonclick(e);
                    
                  }
                }}>
                  Search
                </button>
              </div>
            </div>
          </form>
        </div>
        {searchapi != ''?(
        <div>
        <Trends  search={searchapi} />
        </div>
        ):(
          <Trends/>)
        }
        
      </div>






    </div>




  </>)
}



var stylingObject = {
  searchs: {
    display: "inline-block",
    justifyContent: "center",
    width: "40%",
  }
}
