import Navigation from './Components/All/navigation'
import Nav from './Nav'
import Trends from './Components/Trends/Trends'
import axios from 'axios'
import { useEffect, useState } from 'react'
import Select from 'react-select'
import { Alert, Button, Form, Row, Col, InputGroup, FormControl } from 'react-bootstrap';
// import Button from '@restart/ui/esm/Button'
import { NavLink } from "react-router-dom";

export default function Home() {

  const [search, setSearch] = useState('');
  const [array, setArray] = useState('');
  const [postdata, setPostdata] = useState('');
  const [data,setdata]=useState([]);
  const [searchapi, setSearchapi] = useState('');
  const [loading, setLoading] = useState(false);
  // console.log(options)

  // const option = [
  //   { value: search, label: search },
  //   { value: 'chocolate', label: 'Chocolate' },
  //   { value: 'chocolate', label: 'Chocolate' },
  //   { value: 'chocolate', label: 'Chocolate' },
  // ]


  useEffect(() => {
    // fetchlist();
    searching("")
  }, [])



  // let url = "http://localhost:3001/showfiles/";

  // const fetchlist = () => {
  //   axios.get(url).then(response => {
  //     setArray(response.data);
  //   })
  // }
  // console.log(array);

  // const handleChange = e => {
  //   setSearch(e.target.value);
  // }
  function searchpostonclick(e) {
    e.preventDefault();
    setSearchapi(search);
  }

  function searchpostonclear(e) {
    e.preventDefault();
    setSearch("");
    setSearchapi("");
  }

  const searching= async(text)=>{

    fetch('http://localhost:3001/search', {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json',
      },
      body: JSON.stringify({search:text}),
  }).then((result) => {
      result.json().then((resp) => {
        setdata(resp);
        if(resp == null){
          searching
        }
        console.log(resp,text,"<=====anne waala")
      })
  })
      .catch((error) => {
          console.error('Error:', error);
          searching("NOT FOUND");
      });
  }


  return (<>
    <Nav />
    <Navigation navigation="All Post"/>

    <div className="py-md-5 py-3 bg-light">
      <div className="container px-md-5 mb-5">
        {/* <div className="text-center pb-md-5"> */}
        {/* <div className="searchs" style={stylingObject.searchs} > */}
        {/* <Select option={option} onChange={(e)=>{ handleChange(e); setOptions(e.target.value)}} /> */}
        {/* <Button variant="primary" onClick={(e) => { searchpostonclick(e) }}>Search</Button> */}
        {/* </div> */}
        {/* </div> */}
        {/* <div > */}
        <div className="pb-md-5 pb-3 d-flex justify-content-center" >
          <form>
            <div className="row align-items-center">
              <div className="col" xs="auto">
                <input
                  type="text"
                  className="mb-2 form-control"
                  id="inlineFormInput"
                  placeholder="Search"
                  // value={search}
                  onChange={(e) => { 
                    console.log(e.target.value,"<=====Jaane waala")
                    searching(e.target.value);
                    // setSearch(e.target.value);
                   }}
                />
              </div>
              <div className="col" xs="auto">
                <div class="btn-group" role="group" aria-label="Basic example">
                  <button className="btn btn-danger mr-2 mb-2" onClick={searchpostonclear}>X</button>
                  <button className="btn btn-primary mb-2" onClick={(e) => {
                    if (search == null) {
                      setSearchapi();
                      console.log(searchapi);
                    } else {
                      // setSearchapi('');
                      searchpostonclick(e);
                    }
                  }}>
                    Search
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
        {/* {searchapi != '' ? (
          <div>
            <Trends search={searchapi} />
          </div>
        ) : (
          <Trends />)
        } */}
        <div class="row icon-boxes">
        {data.map((item, i) =>
                            <div key={i} class="col-md-6 col-lg-3 col-sm-4 d-flex align-items-stretch my-3 hover" >
                                <div class="" style={stylingObject.iconBox} >
                                    <div class=""><img src={"/" + item.file_name} className="card-img-top" alt="..." style={{ height: '13rem' }} /></div>
                                    <h6 class="fw-bold mt-3"><NavLink to={"/postview/" + item.id} className="text-decoration-none">{item.title.substring(0, 70) + "..."}</NavLink></h6>
                                    <p class="">{item.description.substring(3, 70) + "..."}</p>
                                    <div className="text-left button-effect"><NavLink to={"/postview/" + item.id} className="text-decoration-none effect effect-1" postid='' >Read More <span className="slide-right">-&gt;</span></NavLink></div>
                    
                                </div>
                            </div>
                        )}
                        </div>
      </div>
    </div>
  </>)
}



var stylingObject = {
  searchs: {
    display: "inline-block",
    justifyContent: "center",
    width: "50%",
  },
  iconBox: {
    padding: "5% 5%",
    position: "relative",
    overflow: "hidden",
    background: "#fff",
    boxShadow: "0 0 29px 0 rgb(18 66 101 / 8%)",
    transition: "all 0.3s ease-in-out",
    borderRadius: "8px",
    zIndex: "1",

    "&:hover": {
        transform: "scale(1.08)",
      },
},
  
}
