import Nav from './Nav';
import Navigation from './Components/All/navigation';
import Username from "./Components/User/Updateprofile";
import {useState,useEffect} from 'react';
export default function User(){

    const [fetchemail,setfetchemail]= useState('');
    
    const [fetchname,setfetchname]= useState('');
    const [name,setName] = useState("");
    const [email,setEmail] = useState("");
    const [password,setPassword]= useState("");

// useEffect(()=>{

// },[])

    const onsubmitform = (e)=>{
        e.preventDefault()
        console.log(email,password)
    }
    console.log(JSON.parse(localStorage.getItem('user-info')).name,"name")

    return(<>
        <Nav/>
        <span className="text-capitalize"><Navigation  navigation={"Edit"}/>
        </span>


        <div class="masthead1 container">
            <div class="row gx-5 align-items-center justify-content-center">
            <div class="col-6 card0 p-md-5">
                <form class="was-validated" >
                    <label for="validationServer03">Name</label>
                    <input type="text" class="form-control is-valid" id="validationServer03" defaultValue={JSON.parse(localStorage.getItem('user-info')).name} placeholder="Enter Name..." required/>
                    <br/>
                    <label for="validationServer01">Email</label>
                    <input type="email" class="form-control is-valid" id="validationServer01" defaultValue={JSON.parse(localStorage.getItem('user-info')).email} placeholder="Enter Email..." onChange={(e)=>{setEmail(e.target.value)}} required/>
                    <br/>
                    <label for="validationServer02">Password</label>
                    <input type="password" class="form-control is-valid" id="validationServer02" placeholder="Enter Password..." onChange={(e)=>{setPassword(e.target.value)}} required/>
                    <br/>
                    
                    <input type="submit" value="Update" onClick={(e)=>onsubmitform(e)}/>
                </form>
            </div>
            </div>
        </div>

    </>);
}
