import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function Updatenewpost(props) {

    const [array, setArray] = useState([]);

    let profile = JSON.parse(localStorage.getItem('profile'));

    async function fetchlist(){
        let url = "http://127.0.0.1:8000/api/showfile/" + props.Postid;
        fetch(url).then(response => response.json()).then(
            data => setArray(data)
        )
    }

    useEffect(() => {
        fetchlist()
    }, [])

    const [error, setError] = useState("");
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const file_name = "doc/iStock-841135958.jpg";
    const account = profile.name;

    
    let {id} = useParams();
    


    async function updatearticle(e) {

        e.preventDefault();
        // axios.post("http://127.0.0.1:8000/api/insertfile",fData)
        // .then((res) => {
        //     console.log("done",res)
        // });
    }

    return (<>
        <section className="">
            <div className=" ">
                <div className="" >
                    <div className=" ">
                        <h1 className="h3 mb-3 font-weight-bold text-center">Update Post</h1>
                        <label for="inputEmail" className="sr-only">Title</label>
                        <input type="text" className="form-control" placeholder={"Enter Title"} defaultValue={array.title} onChange={(e) => setTitle(e.target.value)} /><br />
                        <label for="inputPassword" className="sr-only">Description</label>
                        <input type="text" className="form-control" placeholder="Enter Description" defaultValue={array.description} onChange={(e) => setDescription(e.target.value)} />
                        <br />
                        <button className="btn btn-md btn-primary btn-block" onClick={updatearticle} >Add Post</button>
                        <br /><br />
                        {error ? error : null}



                        {title}<br/>
                        {description}

                    </div>
                </div>
            </div>
        </section>
    </>);
}
