import axios from "axios";
import { useState } from "react";

export default function Imageupload(){

    const[imagedata,setDImagedata]  = useState("");

    const handleChange = (file)=>{
        setDImagedata(file[0]);
    };

    let title = "Testing";
    let description = "Testing";
    let account = "Testing";
    

    const submitData = (e) => {
        e.preventDefault();
        const fData = new FormData();
        fData.append("image",imagedata);
        

        let formdata = {title,description,fData,account}
        axios.post("http://127.0.0.1:8000/api/upload-image",formdata)
        .then((res) => {
            console.log("response",res);
        });
    };
    return(<>
    
        <form onSubmit={submitData}>
            <label>Upload Image</label>
            <input name="image" id="image" type="file" onChange={(e)=> handleChange(e.target.files)}/>
            <button type="submit" onClick={submitData}>Upload </button>
        </form>

    </>);
}
