import Nav from './Nav'
import Trends from './Components/Trends/Trends'
import axios from 'axios'
import { useEffect, useState } from 'react'
import Select from 'react-select'
import { Alert, Button, Form,Row,Col,InputGroup,FormControl } from 'react-bootstrap';
// import Button from '@restart/ui/esm/Button'

export default function Home() {

  const [search, setSearch] = useState('');
  const [array, setArray] = useState('');
  const [options, setOptions] = useState('');

  console.log(options)

  const option = [
    { value: search, label: search },
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'chocolate', label: 'Chocolate' },
  ]


  useEffect(() => {
    fetchlist();
    searchpost();
  }, [])

  const searchpost = () => {
    axios.post('http://localhost:3001/search', search).then(response => {
      console.log(response);
    }).catch(error => {
      console.error("Error", error);
    })
  }

  let url = "http://localhost:3001/showfiles/";

  const fetchlist = () => {
    axios.get(url).then(response => {
      setArray(response.data);
    })
  }
  console.log(array);

  const handleChange = e => {
    setSearch(e.value);
  }
  function searchpostonclick(e) {
    e.preventDefault();

    console.log(search);
  }

  return (<>
    <Nav />

    <div className="masthead1 bg-light">


      <div className="container px-5 mb-5">
        <div className="text-center display-6 fw-bold mb-5 ">ALL POST</div>
        {/* <div className="text-center pb-md-5"> */}
          {/* <div className="searchs" style={stylingObject.searchs} > */}
            {/* <Select option={option} onChange={(e)=>{ handleChange(e); setOptions(e.target.value)}} /> */}
            {/* <Button variant="primary" onClick={(e) => { searchpostonclick(e) }}>Search</Button> */}
          {/* </div> */}
        {/* </div> */}
        {/* <div > */}
        <div className="pb-md-5 d-flex justify-content-center" >
        <Form>
              <Row className="align-items-center">
                <Col xs="auto">
                  <Form.Label htmlFor="inlineFormInput" visuallyHidden>
                    Name
                  </Form.Label>
                  <Form.Control
                    className="mb-2"
                    id="inlineFormInput"
                    placeholder="Search"
                    onChange={(e)=>{setOptions(e.target.value)}}
                  />
                </Col>
                <Col xs="auto">
                  {/* <Button className="mb-2" onClick={(e) => { searchpostonclick(e) }}>
                    Submit
                  </Button> */}
                </Col>
              </Row>
            </Form>
        {option}
        </div>
        <Trends />
      </div>
    </div>

  </>)
}



var stylingObject = {
  searchs: {
    display: "inline-block",
    justifyContent: "center",
    width: "40%",
  }
}
