import { useEffect, useState } from "react";

export default function Updatenewpost(props) {

    const [array, setArray] = useState([]);

    let profile = JSON.parse(localStorage.getItem('profile'));

    useEffect(() => {
        let url = "http://127.0.0.1:8000/api/showfile/" + props.Postid;
        fetch(url).then(response => response.json()).then(
            data => setArray(data)
        )
    }, [])

    const [error, setError] = useState("");
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const file_name = "doc/iStock-841135958.jpg";
    const account = profile.name;

    async function addarticle(e) {
        
        let profile = JSON.parse(localStorage.getItem('profile'));
        let data = localStorage.getItem('user-info');
        const account = profile.name;

        e.preventDefault();
        const fData = new FormData();
        fData.append("image", imagedata);
        fData.append("title", title);
        fData.append("description", description);
        fData.append("account", account);

        // const result = await fetch("http://127.0.0.1:8000/api/upload-image", {
        //     method: "POST",
        //     body: fData,
        // })
        axios.put("http://127.0.0.1:8000/api/insertfile",fData)
        .then((res) => {
            // console.log("response",res);
            // console.log("response",res.data.message);
            if (res.data.message === "Success") {
                    setError(
                        <div class="alert alert-success" role="alert">
                            Inserted Successfully
                        </div>
                    );
                    setTitle("");
                    setDescription("");
                    setDImagedata("");
                }else if (res.data.message === "Select the Image") {
                    setError(
                        <div class="alert alert-danger" role="alert">
                            Upload File
                        </div>
                    );
                }
                else {
                    setError(
                        <div class="alert alert-danger" role="alert">
                            Inserted Unsuccessfully
                        </div>
                    );
        
                }
               
        });

    };



    async function updatearticle(e) {

        e.preventDefault();
    }

    return (<>
        <section className="">
            <div className=" ">
                <div className="" >
                    <div className=" ">
                        <h1 className="h3 mb-3 font-weight-bold text-center">Update Post</h1>
                        <label for="inputEmail" className="sr-only">Title</label>
                        <input type="text" className="form-control" placeholder={"Enter Title"} defaultValue={array.title} onChange={(e) => setTitle(e.target.value)} /><br />
                        <label for="inputPassword" className="sr-only">Description</label>
                        <input type="text" className="form-control" placeholder="Enter Description" defaultValue={array.description} onChange={(e) => setDescription(e.target.value)} />
                        <br />
                        <button className="btn btn-md btn-primary btn-block" onClick={updatearticle} >Add Post</button>
                        <br /><br />
                        {error ? error : null}

                    </div>
                </div>
            </div>
        </section>
    </>);
}
