import { useState } from "react";
import { useEffect } from "react";
import { NavLink } from "react-router-dom";
import { axios } from "axios";

export default function Trends(props) {

    const [sitedata, setSitedata] = useState([])

    const [searchdata, setSearchdata] = useState("")
    const [loading, setLoading] = useState(false);
    const [pname, setPname] = useState("");
    const [token, setToken] = useState("");

    // console.log(props.search)

    useEffect(() => {

        setPname(JSON.parse(localStorage.getItem('user-info')));
        setToken(pname.token);
        showfile();

    }, [])


    let showfile = async () => {
        console.log("token= ", token);
        if (props.count) {
            setSearchdata(props.count);
            let url = "/showfiles/count/" + props.count;
            console.log(url);
            fetch(url, {
                // mode: 'no-cors',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + token,
                },
            }).then((result) => {
                result.json().then((resp) => {
                    setSitedata(resp)
                })
            })
                .catch((error) => {
                    console.error('Error:', error);
                    // setSearchdata("");
                });
            setLoading(true)
        } else if (props.search) {

            setSearchdata(props.search);
            // let sear = searchdata
            // axios.post('http://localhost:3001/search', {
            //     search:sear
            // }).then(response => {
            //     console.log(response);
            //     setSitedata(response.data);
            // }).catch(error => {
            //     console.error("Error", error);
            //     setLoading(true)
            // })
            let sql = {
                search: props.search
            }

            fetch('/search', {
                method: 'POST', // or 'PUT'
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(sql),
            }).then(response => response.json())
                .then(data => {
                    setSitedata(data);
                    if (data.length === 0) {
                        setSearchdata("404");
                    }
                    console.log("sidedara", data);
                })
                .catch((error) => {
                    console.error('Error:', error);
                });
            setLoading(true)
        }
        else {
            setSearchdata('200');
            let url = "/showfiles/";
            console.log(url);
            fetch(url).then((result) => {
                result.json().then((resp) => {
                    setSitedata(resp)
                })
            })
            setLoading(true)
        }
    }




    return (<>

        {loading ? (

            <div>
                {searchdata != 404 || searchdata == 200 ? (
                    // <div className=" row row-cols-2 row-cols-sm-2 row-cols-md-4 g-4">

                    //     {sitedata.map((item, i) =>
                    //         <div className="col-md" key={i}>
                    //             <div className="card" style={{ width: 'rem' }}>
                    //                 <img src={"/" + item.file_name} className="card-img-top" alt="..." style={{ height: '13rem' }} />
                    //                 <div className="card-body">
                    //                     <h6 className="card-title fw-bold p"><NavLink to={"/postview/" + item.id} className="text-decoration-none">{item.title.substring(0, 70) + "..."}</NavLink></h6>
                    //                     <p className="card-text">{item.description.substring(3, 70) + "..."} </p>
                    //                     <div className="text-left button-effect"><NavLink to={"/postview/" + item.id} className="text-decoration-none effect effect-1" postid='' >Read More <span className="slide-right">-&gt;</span></NavLink></div>
                    //                 </div>
                    //             </div>
                    //         </div>
                    //     )}
                    // </div>
                    <div class="row icon-boxes">
                        <div class="col-md-6 col-lg-3 d-flex align-items-stretch mb-5 mb-lg-0 aos-init aos-animate" data-aos="zoom-in" data-aos-delay="200">
                            <div class="icon-box" style={sty}>
                                <div class="icon"><i class="ri-stack-line"></i></div>
                                <h4 class="title"><a href="">Lorem Ipsum</a></h4>
                                <p class="description">Voluptatum deleniti atque corrupti quos dolores et quas molestias excepturi</p>
                            </div>
                        </div>

                        <div class="col-md-6 col-lg-3 d-flex align-items-stretch mb-5 mb-lg-0 aos-init aos-animate" data-aos="zoom-in" data-aos-delay="300">
                            <div class="icon-box">
                                <div class="icon"><i class="ri-palette-line"></i></div>
                                <h4 class="title"><a href="">Sed ut perspiciatis</a></h4>
                                <p class="description">Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore</p>
                            </div>
                        </div>

                        <div class="col-md-6 col-lg-3 d-flex align-items-stretch mb-5 mb-lg-0 aos-init aos-animate" data-aos="zoom-in" data-aos-delay="400">
                            <div class="icon-box">
                                <div class="icon"><i class="ri-command-line"></i></div>
                                <h4 class="title"><a href="">Magni Dolores</a></h4>
                                <p class="description">Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia</p>
                            </div>
                        </div>

                        <div class="col-md-6 col-lg-3 d-flex align-items-stretch mb-5 mb-lg-0 aos-init aos-animate" data-aos="zoom-in" data-aos-delay="500">
                            <div class="icon-box">
                                <div class="icon"><i class="ri-fingerprint-line"></i></div>
                                <h4 class="title"><a href="">Nemo Enim</a></h4>
                                <p class="description">At vero eos et accusamus et iusto odio dignissimos ducimus qui blanditiis</p>
                            </div>
                        </div>

                    </div>
                ) : (
                    <div className=" row text-center">
                        <h2 className="">Not Found</h2>
                    </div>
                )
                }
            </div>
        ) : (
            <div class="d-flex justify-content-center">
                <div class="spinner-border m-5" role="status">
                    <span class="visually-hidden">Loading...</span>
                </div>
            </div>
        )}

    </>);

}

const stringifycss = {
    iconBox : {
        padding: "50px 30px",
        position: "relative",
        overflow: "hidden",
        background: "#fff",
        boxShadow: "0 0 29px 0 rgb(18 66 101 / 8%)",
        transition: "all 0.3s ease-in-out",
        borderRadius: "8px",
        zIndex: "1",
    }

}
