        //             console.log("result",result1);
        //             if(result1 === "success" ){
        //                 setError("success");
        //             }
        //         }
        //     }
        //     );
    }

    return (<>
        <section className="">

            {/* {array.map((item,i)=>{
                <li key={i}>{item}</li>
            })} */}
