import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

export default function Updatenewpost(props) {

    const [array, setArray] = useState([]);

   

    let profile = JSON.parse(localStorage.getItem('profile'));

    async function fetchlist() {
        let url = "http://127.0.0.1:8000/api/showfile/" + props.Postid;
        fetch(url).then(response => response.json()).then(
            data => setArray(data),
        )
    }

    useEffect(() => {
        fetchlist()
    }, [])

    const [error, setError] = useState("");
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");

    // const account = profile.name;
    const [result1, setResult1] = useState("");


    let { id } = useParams();

    const [imagedata, setDImagedata] = useState("");

    const handleChange = (file) => {
        console.log(file[0]);
        setDImagedata(file[0]);
    };
    
    const handleChangeckeditor = (e, editor) => {
        const data = editor.getData();
        setDescription(data);
    }

    function updatearticle(img,e) {
    
        e.preventDefault();
        const fData = {
            "title":title,
            "description":description,
            "account":"ashish"
        };

        // axios.put("http://127.0.0.1:8000/api/updatefile/58",fData)
        // .then((res) => {
        //     console.log("done",res)
        // });
        // console.warn(fData)
        const requestOptions = {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(fData)
        };
        let url = "http://127.0.0.1:8000/api/updatefile/" + props.Postid;
        fetch(url, requestOptions)
            .then(response => response.json())
            .then(data => {
                setResult1(data.message);
                console.log("result",data);
                // if(result1 === "success" ){
                //     console.log("done",result1);
                //     setError("psk");
                // }
            }
            );
    }

    return (<>
        <section className="">
            <div className=" ">
                <div className="" >
                    <div className=" ">
                        <h1 className="h3 mb-3 font-weight-bold text-center">Update Post</h1>
                        <label for="inputEmail" className="sr-only">Title</label>
                        <input type="text" className="form-control" placeholder={"Enter Title"} defaultValue={array.title} onChange={(e) => {console.log(e.target.value); setTitle(e.target.value)}} /><br />
                        <label for="" className="sr-only">Description</label>
                        {/* <input type="text" className="form-control" placeholder="Enter Description" value={description} onChange={(e) => setDescription(e.target.value)} /><br /> */}

                        <CKEditor editor={ClassicEditor} data={array.description} onChange={handleChangeckeditor} />
                        <br />

                        <label for="uploadfile" className="sr-only">Upload File </label>
                        <input name="image" id="image" type="file" onChange={(e) => handleChange(e.target.files)} />
                        <br />

                        <button className="btn btn-md btn-primary btn-block" onClick={(e)=>{updatearticle(imagedata,e)}} >Update Post</button>
                        <br /><br />
                        {error!=null&&  <div class="alert alert-success" role="alert">
                            Inserted Successfully
                        </div>}
                        {id}
                        {title}<br />
                        {description}<br />

                    </div>
                </div>
            </div>
        </section>
    </>);
}
