import Navigation from './Components/All/navigation'
import Nav from './Nav'
import Trends from './Components/Trends/Trends'
import axios from 'axios'
import { useEffect, useState } from 'react'
import Select from 'react-select'
import { Alert, Button, Form, Row, Col, InputGroup, FormControl } from 'react-bootstrap';
// import Button from '@restart/ui/esm/Button'
import { NavLink } from "react-router-dom";

export default function Home() {

  const [search, setSearch] = useState('');
  const [array, setArray] = useState('');
  const [postdata, setPostdata] = useState('');
  const [data, setdata] = useState([]);
  const [searchapi, setSearchapi] = useState('');
  const [loading, setLoading] = useState(false);



  useEffect(() => {
    searching("")
  }, [])



  function searchpostonclick(e) {
    e.preventDefault();
    setSearchapi(search);
  }

  function searchpostonclear(e) {
    e.preventDefault();
    setSearch("");
    setSearchapi("");
  }

  const searching = async (text) => {

    fetch('http://localhost:3001/search', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ search: text }),
    }).then((result) => {
      result.json().then((resp) => {
        if (resp[0].status === 404) {
          setdata(404)
        } else {
          setdata(resp);
        }
      })
    })
      .catch((error) => {
        console.error('Error:', error);

      });
  }

  console.log(data, "dat")

  return (<>
    <Nav />
    <Navigation navigation="All Post" />

    <div className="py-md-5 py-3 bg-light">
      <div className="container px-md-5 mb-5">

        <div className="pb-md-5 pb-3 d-flex justify-content-center" >
          <form>
            
            <div className="row align-items-center">
              <div className="col-5" xs="auto">
                <input
                  type="text"
                  className="mb-2 form-control "
                  id="inlineFormInput"
                  placeholder="Search"
                  onChange={(e) => {
                    searching(e.target.value);
                  }}
                />
              </div>
              <div className="col" xs="auto">
                <button className="btn btn-primary mb-2" onClick={(e) => {
                  if (search == null) {
                    setSearchapi();
                    console.log(searchapi);
                  } else {
                    searchpostonclick(e);
                  }
                }}>
                  Search
                  </button>

              </div>
            </div>
          </form>
        </div>
        <div class="row icon-boxes">

          {
            data != 404 ? (
              data.map((item, i) =>
                <div key={i} class="col-md-6 col-lg-3 col-sm-4 d-flex align-items-stretch my-3 hover" >
                  <div class="" style={stylingObject.iconBox} >
                    <div class=""><img src={"/" + item?.file_name} className="card-img-top" alt="..." style={{ height: '13rem' }} /></div>
                    <h6 class="fw-bold mt-3"><NavLink to={"/postview/" + item.id} className="text-decoration-none">{item?.title.substring(0, 70) + "..."}</NavLink></h6>
                    <p class="">{item?.description.substring(3, 70) + "..."}</p>
                    <div className="text-left button-effect"><NavLink to={"/postview/" + item?.id} className="text-decoration-none effect effect-1" postid='' >Read More <span className="slide-right">-&gt;</span></NavLink></div>
                  </div>
                </div>
              )) : (<div class="h1 text-center">Not Found</div>)}
        </div>
      </div>
    </div>
  </>)
}



var stylingObject = {
  searchs: {
    display: "inline-block",
    justifyContent: "center",
    width: "50%",
  },
  iconBox: {
    padding: "5% 5%",
    position: "relative",
    overflow: "hidden",
    background: "#fff",
    boxShadow: "0 0 29px 0 rgb(18 66 101 / 8%)",
    transition: "all 0.3s ease-in-out",
    borderRadius: "8px",
    zIndex: "1",

    "&:hover": {
      transform: "scale(1.08)",
    },
  },

}
