import { useEffect, useState } from "react";

import Username from "../User/Username";
export default function Addnewpost() {

    const [error, setError] = useState("");
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [file_name, setFile_name] = useState("");



    const [imagedata, setDImagedata] = useState("");

    const handleChange = (file) => {
        setDImagedata(file[0]);
    };

    const  submitData = async(e) => {
        e.preventDefault();
        const fData = new FormData();
        fData.append("image", imagedata);
        fData.append("title", title);
        fData.append("description", description);
        fData.append("account", account);

        await fetch("http://127.0.0.1:8000/api/upload-image", {
            method: "POST",
            body: fData,
        })
        console.log(fData);
    };

    // const photoUpload = (e) => {
    //     e.preventDefault();
    //     const reader = new FileReader();
    //     const file = e.target.files[0];
    //     console.log("reader", reader)
    //     console.log("file", file)
    //     if (reader !== undefined && file !== undefined) {
    //         reader.onloadend = () => {
    //             setFile_name(file);
    //             // setFile(file)
    //             // setSize(file.size);
    //             // setName(file.name)
    //             // setImagePreview(
    //             console.log(reader.result)
    //         }
    //         reader.readAsDataURL(file);
    //     }
    // }

    async function addarticle(e) {

        e.preventDefault();

        let profile = JSON.parse(localStorage.getItem('profile'));
        let data = localStorage.getItem('user-info');
        const account = profile.name;

        // const filedata = new FormData();
        // filedata.append("file_name",file_name)

        let info = JSON.parse(data);
        let postdata = { title, description, file_name, account }
        let result = await fetch('http://127.0.0.1:8000/api/insertfile', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
                'Authorization': 'Bearer ' + info.access_token
            },
            body: JSON.stringify(postdata)
        });

        result = await result.json();
        if (result.message === "Inserted Successfully") {
            setError(
                <div class="alert alert-success" role="alert">
                    Inserted Successfully
                </div>
            );
            setTitle("");
            setDescription("");
        } else if (result.message === "Upload File") {
            setError(
                <div class="alert alert-danger" role="alert">
                    Upload File
                </div>
            );
        }
        else {
            setError(
                <div class="alert alert-danger" role="alert">
                    Inserted Unsuccessfully
                </div>
            );

        }

        console.log(postdata)
    }

    return (<>
        <Username />
        <section className="">
            <div className=" ">
                <div className="" >
                    <div className=" ">
                        <h1 className="h3 mb-3 font-weight-bold text-center">Add Post</h1>
                        <label for="" className="sr-only">Title</label>
                        <input type="text" className="form-control" placeholder="Enter Title" value={title} onChange={(e) => setTitle(e.target.value)} /><br />
                        <label for="" className="sr-only">Description</label>
                        <input type="text" className="form-control" placeholder="Enter Description" value={description} onChange={(e) => setDescription(e.target.value)} /><br />
                        <label for="uploadfile" className="sr-only">Upload File </label>
                        <input name="image" id="image" type="file" onChange={(e) => handleChange(e.target.files)} />
                        <br />
                        <button className="btn btn-md btn-primary btn-block" onClick={addarticle} >Add Post</button>
                        <br /><br />
                        {error ? error : null}
                    </div>
                </div>
            </div>
        </section>
    </>);
}
