import Nav from './Nav'
import Trends from './Components/Trends/Trends'
import axios from 'axios'
import { useEffect, useState } from 'react'
import Select from 'react-select'
import { Alert, Button, Form, Row, Col, InputGroup, FormControl } from 'react-bootstrap';
// import Button from '@restart/ui/esm/Button'

export default function Home() {

  const [search, setSearch] = useState('');
  const [array, setArray] = useState('');
  const [options, setOptions] = useState('');

  // console.log(options)

  // const option = [
  //   { value: search, label: search },
  //   { value: 'chocolate', label: 'Chocolate' },
  //   { value: 'chocolate', label: 'Chocolate' },
  //   { value: 'chocolate', label: 'Chocolate' },
  // ]


  useEffect(() => {
    fetchlist();
  }, [])

  

  let url = "http://localhost:3001/showfiles/";

  const fetchlist = () => {
    axios.get(url).then(response => {
      setArray(response.data);
    })
  }
  console.log(array);

  // const handleChange = e => {
  //   setSearch(e.target.value);
  // }
  function searchpostonclick(e) {
    e.preventDefault();
    
      let seac= {search}
      axios.post('http://localhost:3001/search', seac).then(response => {
        console.log(response);
      }).catch(error => {
        console.error("Error", error);
      })

    
        console.log("search", seac);
  }

  return (<>
    <Nav />
    <div className="masthead1 bg-light">


      <div className="container px-5 mb-5">
        <div className="text-center display-6 fw-bold mb-5 ">ALL POST</div>
        {/* <div className="text-center pb-md-5"> */}
        {/* <div className="searchs" style={stylingObject.searchs} > */}
        {/* <Select option={option} onChange={(e)=>{ handleChange(e); setOptions(e.target.value)}} /> */}
        {/* <Button variant="primary" onClick={(e) => { searchpostonclick(e) }}>Search</Button> */}
        {/* </div> */}
        {/* </div> */}
        {/* <div > */}
        <div className="pb-md-5 d-flex justify-content-center" >
          <form>
            <div className="row align-items-center">
              <div className="col" xs="auto">
                <input
                  type="text"
                  className="mb-2 form-control"
                  id="inlineFormInput"
                  placeholder="Search"
                  onChange={(e) => { setSearch(e.target.value) }}
                />
              </div>
              <div className="col" xs="auto">
                <button className="btn btn-primary mb-2" onClick={(e) => {
                  if (search == '') {
                    alert("Enter something to search");
                  } else {
                    searchpostonclick(e);
                  }
                }}>
                  Search
                </button>
              </div>
            </div>
          </form>
          {search}
        </div>
        <Trends />
      </div>



      {loading ? (
                    <div className=" row row-cols-1 row-cols-md-3 g-4">

                        {sitedata.map((item, i) =>
                            <div className="col-md" key={i}>
                                <div className="card" style={{ width: 'rem' }}>
                                    <img src={"http://127.0.0.1:3001/" + item.file_name} className="card-img-top" alt="..." style={{ height: '13rem' }} />
                                    <div className="card-body">
                                        <h5 className="card-title fw-bold"><NavLink to={"/postview/" + item.id} className="text-decoration-none">{item.title.substring(0, 70) + "..."}</NavLink></h5>
                                        <p className="card-text">{item.description.substring(3, 70) + "..."} </p>
                                        <div className="text-center"><NavLink to={"/postview/" + item.id} class="btn btn-primary" postid='' >Read More</NavLink></div>
                                    </div>
                                </div>
                            </div>
                        )}
                        </div>
            ) : (
                <div class="d-flex justify-content-center">
                    <div class="spinner-border m-5" role="status">
                        <span class="visually-hidden">Loading...</span>
                    </div>
                </div>
            )}


    </div>




  </>)
}



var stylingObject = {
  searchs: {
    display: "inline-block",
    justifyContent: "center",
    width: "40%",
  }
}
