import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
// import { response } from "../../../../server1/app";
import InputTags from "react-input-tags-hooks";
import 'react-input-tags-hooks/build/index.css'; // required

export default function Updatenewpost(props) {

    const [array, setArray] = useState('');
    const [pname, setPname] = useState('');
    const [tags, setTags] = useState([])
    const getTags = (tags) => {
        setTags(tags);
      }

    let profile = JSON.parse(localStorage.getItem('user-info'));

    // async function fetchlist() {
        let url = "http://localhost:3001/showfiles/" + props.Postid;
    //     fetch(url).then(response => response.json()).then(
    //         data => { console.log(data); setArray(data); setTitle(data); },
    //     )
    // }

    const fetchlist = ()=>{
        axios.get(url).then(response=>{
            // console.log(response.data[0]);
            setArray(response.data[0])
        })
    }

    useEffect(() => {
        fetchlist();
        setPname(JSON.parse(localStorage.getItem('user-info')));
    }, [])

    const [error, setError] = useState("");
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");

    // const account = profile.name;
    const [result1, setResult1] = useState("");


    let { id } = useParams();

    const [imagedata, setDImagedata] = useState("");

    // const handleChange = (file) => {
    //     console.log(file[0]);
    //     setDImagedata(file[0]);
    // };

    const handleChangeckeditor = (e, editor) => {
        const data = editor.getData();
        setDescription(data);
    }

    async function handlefile(file) {
        // let file = e.target.files[0];
        setDImagedata(file[0]);
        setResult1(URL.createObjectURL(file[0]))
        // console.log(file)
    }

    async function updatearticle(e) {
        e.preventDefault();
        // const fData = {
        //     "title": title,
        //     "description": description,
        //     "account": "ashish",
        //     "filename": imagedata
        // };
        
        const fData = new FormData();
        fData.append("filename", imagedata);
        fData.append("title", title);
        fData.append("description", description);
        fData.append("account", pname);
        fData.append("tag", tags);
        console.log(fData);
        for (var pair of fData.entries()) {
            console.log(pair[0]+ ', ' + pair[1]); 
        }

        let url = "http://localhost:3001/updatedata/" + props.Postid;
        axios.post(url, fData)
            .then((res) => {
                // if (res.data.message != undefined) {
                //     setResult1(res.data.message);
                //     console.log("result", result1);
                //     if (result1 === "success") {
                //         setError("success");
                //     }
                // }
                console.log(res);
            });
            console.log(fData)
        // const requestOptions = {
        //     method: 'PUT',
        //     headers: { 'Content-Type': 'application/json' },
        //     body: JSON.stringify(fData)
        // };
        // fetch(url, requestOptions)
        //     .then(response => response.json())
        //     .then(data => {
        //         if(data.message!=undefined){
        //             setResult1(data.message);
        //             console.log("result",result1);
        //             if(result1 === "success" ){
        //                 setError("success");
        //             }
        //         }
        //     }
        //     );
    }

    return (<>
        <section className="">

            <div className=" ">
                <div className="" >
                    <div className=" ">
                        <h1 className="h3 mb-3 font-weight-bold text-center">Update Post</h1>
                        <label for="inputEmail" className="sr-only">Title</label>
                        <input type="text" className="form-control" placeholder={"Enter Title"} defaultValue={array.title} onChange={(e) => { console.log(e.target.value); setTitle(e.target.value) }} /><br />
                        <label for="" className="sr-only">Description</label>

                        <CKEditor editor={ClassicEditor} data={array.description} onChange={handleChangeckeditor} />
                        <br />

                        <label for="uploadfile" className="sr-only">Upload File </label>
                        <input name="file_name" id="filename" type="file" onChange={(e) => handlefile(e.target.files)} />
                        <br />

                        <button className="btn btn-md btn-primary btn-block" onClick={(e) => { updatearticle(e) }} >Update Post</button>
                        <br /><br />
                        {
                            error === "success" ? (<div class="alert alert-success" role="alert">
                                Inserted Successfully
                            </div>) : null
                        }
                        {error}

                        {title}

                        <img src={result1}/>
                    </div>
                </div>
            </div>
        </section>
    </>);
}
