import { useState } from "react";
import { useEffect } from "react";
import { NavLink } from "react-router-dom";
import { axios } from "axios";

export default function Trends(props) {

    const [sitedata, setSitedata] = useState([])

    const [searchdata, setSearchdata] = useState('live')
    const [loading, setLoading] = useState(false);

    // console.log(props.search)

    useEffect(() => {
        showfile();
    }, [])

    let showfile = async () => {
        if (props.count) {
            let url = "http://localhost:3001/showfiles/count/" + props.count;
            console.log(url);
            fetch(url).then((result) => {
                result.json().then((resp) => {
                    setSitedata(resp)
                })
            })
            setLoading(true)
        } else if (props.search) {
            console.log("props type ", typeof (props.search))
            
            console.log("props ", searchdata)
            let sear = searchdata
            axios.post('http://localhost:3001/search', {sear}).then(response => {
                console.log(response);
                setSitedata(response.data);
            }).catch(error => {
                console.error("Error", error);
                setLoading(true)
            })


            // fetch('http://localhost:3001/search', {
            //     method: 'POST', // or 'PUT'
            //     headers: {
            //         'Content-Type': 'application/json',
            //     },
            //     body: JSON.stringify(props.search),
            // }).then(response => response.json())
            //     .then(data => {
            //         setSitedata(data);
            //     console.log("sidedara",data);
            //     })
            //     // .catch((error) => {
            //     //     console.error('Error:', error);
            //     // });

            //     console.log("props",props.search);
            //     console.log("sidedara",sitedata);

        }
        else {
            let url = "http://localhost:3001/showfiles/";
            console.log(url);
            fetch(url).then((result) => {
                result.json().then((resp) => {
                    setSitedata(resp)
                })
            })
            setLoading(true)
        }
    }




    return (<>

        {loading ? (
            <div className=" row row-cols-1 row-cols-md-3 g-4">

                {sitedata.map((item, i) =>
                    <div className="col-md" key={i}>
                        <div className="card" style={{ width: 'rem' }}>
                            <img src={"http://127.0.0.1:3001/" + item.file_name} className="card-img-top" alt="..." style={{ height: '13rem' }} />
                            <div className="card-body">
                                <h5 className="card-title fw-bold"><NavLink to={"/postview/" + item.id} className="text-decoration-none">{item.title.substring(0, 70) + "..."}</NavLink></h5>
                                <p className="card-text">{item.description.substring(3, 70) + "..."} </p>
                                <div className="text-center"><NavLink to={"/postview/" + item.id} class="btn btn-primary" postid='' >Read More</NavLink></div>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        ) : (
            <div class="d-flex justify-content-center">
                <div class="spinner-border m-5" role="status">
                    <span class="visually-hidden">Loading...</span>
                </div>
            </div>
        )}

    </>);
}
