import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";

export default function Listpost() {

  const [postJSON, setPostJSON] = useState([]);
  const [message,setMessage] = useState("");

  function deletefile(){
    let data = localStorage.getItem('user-info');
    let info = JSON.parse(data);
    const deleteImage = (productId) =>
  {
    axios.get('http://localhost:8000/api/delete/'+productId)
    .then((result)=>{
      loadList();
    })
    .catch(()=>{
      alert('Error in the Code');
    });
  };

    let header = {
      "Content-Type": "application/json",
      "Accept": "application/json",
      'Authorization': 'Bearer ' + info.access_token
    }
    let url = "http://127.0.0.1:8000/api/deletefile/" + id;
    fetch(url, { header }).then(response => response.json()).then(data => setMessage(data))

  }

  useEffect(() => {
    let data = localStorage.getItem('user-info');
    let info = JSON.parse(data)
    const header = { 'Content-Type': 'application/json', 'Authorization': 'Bearer ' + info.access_token }
    fetch('http://127.0.0.1:8000/api/showfile/', { header })
      .then(response => response.json())
      .then(data1 => setPostJSON(data1))
  }, []);



  return (<>
    <table class="table">
      <thead>
        <tr>
          <th scope="col">Sr.No</th>
          <th scope="col">Image</th>
          <th scope="col">Title</th>
          <th scope="col">View</th>
          <th scope="col">Edit</th>
          <th scope="col">Delete</th>
        </tr>
      </thead>
      <tbody>
        {
          postJSON.map((item, i) =>

            <tr key={i}>
              <th scope="row">{i + 1}</th>
              <th><img src={"http://127.0.0.1:8000/" + item.file_name} alt="..." width="50" />
              </th>
              <td>{item.title}</td>
              <td><NavLink to={"/postview/" + item.id}>View</NavLink></td>
              <td><NavLink to={"/postedit/" + item.id}>Edit</NavLink></td>
              <td><NavLink to={"/Deletepost/" + item.id} >Delete</NavLink></td>
            </tr>
          )}
      </tbody>
    </table>


  </>);
}
